            var siteMenuClone = function() {
                $('.js-clone-nav').each(function() {
                    var $this = $(this);
                    $this.clone().attr('class', 'site-nav-wrap').appendTo('.site-mobile-menu-body');
                });
                setTimeout(function() {
                    var counter = 0;
                    $('.site-mobile-menu .has-children').each(function(){
                        var $this = $(this);
                        $this.prepend('<span class="arrow-collapse collapsed">');
                        $this.find('.arrow-collapse').attr({
                            'data-toggle' : 'collapse',
                            'data-target' : '#collapseItem' + counter,
                        });
                        $this.find('> ul').attr({
                            'class' : 'collapse',
                            'id' : 'collapseItem' + counter,
                        });
                        counter++;
                    });
                }, 1000);

                $('body').on('click', '.arrow-collapse', function(e) {
                    var $this = $(this);
                    if ( $this.closest('li').find('.collapse').hasClass('show') ) {$this.removeClass('active');}
                    else {$this.addClass('active');}
                    e.preventDefault();
                });
                $(window).resize(function() {
                    var $this = $(this), w = $this.width();
                    if ( w > 768 ) {if ( $('body').hasClass('offcanvas-menu') ) {$('body').removeClass('offcanvas-menu');}}
                })

                $('body').on('click', '.js-menu-toggle', function(e) {
                    var $this = $(this);
                    e.preventDefault();
                    if ( $('body').hasClass('offcanvas-menu') ) {
                        $('body').removeClass('offcanvas-menu');
                        $this.removeClass('active');
                    } else {
                        $('body').addClass('offcanvas-menu');
                        $this.addClass('active');
                    }
                })
                // click outisde offcanvas
                $(document).mouseup(function(e) {
                    var container = $(".site-mobile-menu");
                    if (!container.is(e.target) && container.has(e.target).length === 0) {
                        if ( $('body').hasClass('offcanvas-menu') ) {$('body').removeClass('offcanvas-menu');}
                    }
                });
            };
            siteMenuClone();
            var siteCarousel = function () {
                if ( $('.nonloop-block-13').length > 0 ) {
                    $('.nonloop-block-13').owlCarousel({
                        center: false,
                        items: 1,
                        loop: true,
                        stagePadding: 0,
                        margin: 0,
                        smartSpeed: 1000,
                        autoplay: true,
                        nav: true,
                        navText: ['<span class="icon-arrow_back">', '<span class="icon-arrow_forward">'],
                        responsive:{
                            600:{
                                margin: 0,
                                nav: true,
                                items: 2
                            },
                            1000:{
                                margin: 0,
                                stagePadding: 0,
                                nav: true,
                                items: 2
                            },
                            1200:{
                                margin: 0,
                                stagePadding: 0,
                                nav: true,
                                items: 3
                            }
                        }
                    });
                }

                $('.slide-one-item').owlCarousel({
                    center: false,
                    items: 1,
                    loop: true,
                    stagePadding: 0,
                    margin: 0,
                    smartSpeed: 1100,
                    autoplay: true,
                    pauseOnHover: false,
                    dots: true,
                    nav: true,
                    navText: ['<span class="icon-keyboard_arrow_left">', '<span class="icon-keyboard_arrow_right">']
                });

                if ( $('.owl-all').length > 0 ) {
                    $('.owl-all').owlCarousel({
                        center: false,
                        items: 1,
                        loop: false,
                        stagePadding: 0,
                        margin: 0,
                        autoplay: false,
                        nav: false,
                        dots: true,
                        touchDrag: true,
                        mouseDrag: true,
                        smartSpeed: 1000,
                        navText: ['<span class="icon-arrow_back">', '<span class="icon-arrow_forward">'],
                        responsive:{
                            400: {
                                margin: 30,
                                nav: false,
                                responsiveRefreshRate: 10,
                                items: 1
                            },
                            768:{
                                margin: 30,
                                stagePadding: 0,
                                nav: false,
                                responsiveRefreshRate: 10,
                                touchDrag: false,
                                mouseDrag: false,
                                items: 2
                            },
                            992:{
                                margin: 30,
                                stagePadding: 0,
                                nav: false,
                                responsiveRefreshRate: 10,
                                touchDrag: false,
                                mouseDrag: false,
                                items: 4
                            },
                            1200:{
                                margin: 30,
                                stagePadding: 0,
                                nav: false,
                                responsiveRefreshRate: 10,
                                touchDrag: false,
                                mouseDrag: false,
                                items: 4
                            }
                        }
                    });
                }
                if ( $('.owl-all-2').length > 0 ) {
                    $('.owl-all-2').owlCarousel({
                        center: false,
                        items: 1,
                        loop: false,
                        stagePadding: 0,
                        margin: 0,
                        autoplay: false,
                        nav: true,
                        dots: true,
                        touchDrag: true,
                        mouseDrag: true,
                        smartSpeed: 1000,
                        navText: ['<span class="icon-arrow_back">', '<span class="icon-arrow_forward">'],
                        responsive:{
                            768:{
                                margin: 30,
                                nav: false,
                                responsiveRefreshRate: 10,
                                items: 1
                            },
                            992:{
                                margin: 30,
                                stagePadding: 0,
                                nav: false,
                                responsiveRefreshRate: 10,
                                touchDrag: false,
                                mouseDrag: false,
                                items: 3
                            },
                            1200:{
                                margin: 30,
                                stagePadding: 0,
                                nav: false,
                                responsiveRefreshRate: 10,
                                touchDrag: false,
                                mouseDrag: false,
                                items: 3
                            }
                        }
                    });
                }
            };
            siteCarousel();
            var siteSticky = function() {$(".js-sticky-header").sticky({topSpacing:0});};
            siteSticky();

            // navigation
            var OnePageNavigation = function() {
                var navToggler = $('.site-menu-toggle');
                $("body").on("click", ".main-menu li a[href^='#'], .smoothscroll[href^='#'], .site-mobile-menu .site-nav-wrap li a[href^='#']", function(e) {
                    e.preventDefault();
                    var hash = this.hash;
                    $('html, body').animate({'scrollTop': $(hash).offset().top - 50}, 600, 'easeInOutExpo', function() {
                        // window.location.hash = hash;
                    });
                });
            };
            OnePageNavigation();

            var siteScroll = function() {
                $(window).scroll(function() {
                    var st = $(this).scrollTop();
                    if (st > 100) {$('.js-sticky-header').addClass('shrink');}
                    else {$('.js-sticky-header').removeClass('shrink');}
                })
            };
            siteScroll();

            $(".animsition").animsition({
                inClass: 'fade-in',
                outClass: 'fade-out',
                inDuration: 1500,
                outDuration: 800,
                linkElement: '.animsition-link',
                loading: true,
                loadingParentElement: 'html',
                loadingClass: 'animsition-loading-1',
                loadingInner: '<div class="loader05"></div>',
                timeout: false,
                timeoutCountdown: 3000,
                onLoadEvent: true,
                browser: [ 'animation-duration', '-webkit-animation-duration'],
                overlay : false,
                overlayClass : 'animsition-overlay-slide',
                overlayParentElement : 'html',
                transition: (url) => { window.location.href = url; }
            });

            let scrollTop = () => {
                let target = document.querySelector("#services-section");
                let target_2 = document.querySelector("#about-section");
                let target_3 = document.querySelector("#contact-section")
                let target_4 = document.querySelector(".footer");
                let scrollToTopBtn = document.querySelector(".scrollToTopBtn")
                let rootElement = document.documentElement

                let callback = (entries, observer) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {scrollToTopBtn.classList.add("showBtn")}
                        else {scrollToTopBtn.classList.remove("showBtn")}
                    });
                }
                let scrollToTop = () => {rootElement.scrollTo({top: 0, behavior: "smooth"})}
                scrollToTopBtn.addEventListener("click", scrollToTop);

                let observer = new IntersectionObserver(callback);
                observer.observe(target);
                observer.observe(target_2);
                observer.observe(target_3);
                observer.observe(target_4);
            }
            scrollTop();

            $("input[type='radio']").on("click",function(){
                var sim = $("input[type='radio']:checked").val();
                if (sim<3) { $('.myratings').css('color','red'); $(".myratings").text(sim); }
                else{ $('.myratings').css('color','green'); $(".myratings").text(sim); }
            });
        </script>
    </body>
</html>
