            .showBtn {opacity: 1;transform: translateY(0)}
            #feedback {float: left;position: fixed;top: calc(50% - 47px);right: 0;z-index: 1000;}
            #feedback a {background: #0072bc;border-radius: 5px 0 0 5px;box-shadow: 0 0 3px rgba(0, 0, 0, .3);border: 3px solid #fff;border-right: 0;display: block;padding: 20px 12px;transition: all .2s ease-in-out;}
            #feedback a:hover {padding-right: 20px;}
            fieldset, label {margin: 0;padding: 0;}
            .rating {border: none;margin-right: 49px;}
            .myratings {color: green;}
            .rating>[id^="star"] {display: none;}
            .rating>label:before {margin: 5px;font-size: 2.25em;font-family: FontAwesome;display: inline-block;content: "\f005";}
            .rating>.half:before {content: "\f089";position: absolute;}
            .rating>label {color: #ddd;float: right;}
            .rating>[id^="star"]:checked~label, .rating:not(:checked)>label:hover, .rating:not(:checked)>label:hover~label {color: #FFD700;}
            .rating>[id^="star"]:checked+label:hover, .rating>[id^="star"]:checked~label:hover, .rating>label:hover~[id^="star"]:checked~label, .rating>[id^="star"]:checked~label:hover~label {color: #FFED85;}
            .reset-option {display: none;}
            .card {position: relative;display: flex;width: 350px;flex-direction: column;min-width: 0;word-wrap: break-word;background-color: #fff;border: none;}
        </style>
